/// <summary>
/// Logic for converting a <see cref="string"/> for Generation 1 &amp; 2 games.
/// </summary>
public static class StringConverter12
{
    public static bool GetIsG1Japanese(ReadOnlySpan<char> str) => AllCharsInDictionary(str, U2RBY_J);
    public static bool GetIsG1English(ReadOnlySpan<char> str)
    {
        var chntable = U2RBC_CHN;
        var engtable = U2RBY_U;
        foreach (var c in str)
        {
            bool chineseChar = false;
            foreach (var dict in chntable)
            {
                if (!dict.ContainsKey(c))
                    continue;
                chineseChar = true;
                break;
            }
            if (!chineseChar)
            {
                if (!engtable.ContainsKey(c))
                    return false;
            }
        }
        return true;
    }
    public static bool GetIsG1Japanese(ReadOnlySpan<byte> raw) => AllCharsInDictionary(raw, RBY2U_J);
    public static bool GetIsG1English(ReadOnlySpan<byte> raw)
    {
        for (int i = 0; i < raw.Length; i++)
        {
            var d = RBY2U_U;
            var c = raw[i];
            if (c == 0)
                break;
            if(c >= 0x01 && c <= 0x2E && c != 0x14 && c != 0x15 && c != 0x16 && c != 0x17 && c != 0x20 && c != 0x21 && c != 0x22 && c != 0x23 && c != 0x24 && c != 0x25 && c != 0x26 && c != 0x27)
            {
                d = RBC2U_CHN[c];
                c = raw[++i];
            }    
            if (!d.ContainsKey(c))
                return false;
        }
        return true;
    }

    private static bool AllCharsInDictionary(ReadOnlySpan<char> c, IReadOnlyDictionary<char, byte> d)
    {
        foreach (var x in c)
        {
            if (!d.ContainsKey(x))
                return false;
        }
        return true;
    }

    private static bool AllCharsInDictionary(ReadOnlySpan<byte> data, IReadOnlyDictionary<byte, char> d)
    {
        foreach (var c in data)
        {
            if (c == 0)
                break;
            if (!d.ContainsKey(c))
                return false;
        }
        return true;
    }

